import React, { Component } from "react";
import LiquidityPool from "./abis/LiquidityPool.json";
import detectEthereumProvider from '@metamask/detect-provider';
import Web3 from 'web3';
import { Grid, Button, Card, Header, HeaderSubheader } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';


import "./App.css";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      account: '',
      LPContract: null,
      address: "",
      tokenA: "",
      tokenB: "",
      pairAddress: "",
      valueToMint: 0,
      valueToApprove: 0,
      valueToAdd: 0
    }
  }

  async componentDidMount() {
    await this.loadWeb3();
    await this.loadBlockchainData();
  }

  // first up is to detect ethereum provider
  async loadWeb3() {
    const provider = await detectEthereumProvider();

    // modern browsers
    if (provider) {
      console.log('Ethereum wallet is connected');

      window.web3 = new Web3(provider);
    } else {
      console.log('No ethereum wallet detected');
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3;
    const accounts = await window.web3.eth.getAccounts();
    this.setState({ account: accounts[0] })
    const LPContract = new web3.eth.Contract(LiquidityPool.abi, "0x207315B508E44FF6Cf4384426b5E3356b1E6E281");
    this.setState({ LPContract });
  }

  handleInputChange = (event) => {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value
    })
  }

  createPair = async() => {
    await this.state.LPContract.methods.createNewPair().send({from: this.state.account});

    const t1 = await this.state.LPContract.methods.tokenA().call()
    const t2 = await this.state.LPContract.methods.tokenB().call()

    this.setState({
      tokenA: t1,
      tokenB: t2
    })
  }

  approveRouter = async() => {
    await this.state.LPContract.methods.approveRouter(10000).send({from: this.state.account});
  }

  mint = async() => {
    await this.state.LPContract.methods.mint(10000).send({from: this.state.account});
  }

  addLiquidity = async() => {
    await this.state.LPContract.methods.addLiquidity(10000).send({from: this.state.account});
  }

  render() {
    return (
      <div className="App">
        <br></br>
        <Header as='h1'>Velvet.Capital</Header>
        <HeaderSubheader as='h2'>Create and Burn</HeaderSubheader>

        <Grid divided='vertically'>
          <Grid.Row columns={2} style={{ margin: "20px" }}>
            <Grid.Column>

              <Card.Group>
                <Card style={{ width: "900px" }}>
                  <Card.Content style={{ background: "#406ccd" }}>
                    <Card.Header>DeFi</Card.Header>
                    <Card.Meta>Portfolio of top DeFi tokens</Card.Meta>
                    <Card.Description>
                      <Button onClick={this.createPair} color="green" type="submit" style={{ margin: "20px" }}>Create Pool!</Button><br></br>
                      <Button onClick={this.mint} color="green" type="submit" style={{ margin: "20px" }}>Mint!</Button><br></br>
                      <Button onClick={this.approveRouter} color="green" type="submit" style={{ margin: "20px" }}>Approve!</Button><br></br>
                      <Button onClick={this.addLiquidity} color="green" type="submit" style={{ margin: "20px" }}>Add Liquidity!</Button><br></br>
                    </Card.Description>
                  </Card.Content>
                </Card>
              </Card.Group>
            </Grid.Column>


            <Grid.Column>
              <Card.Group>
                <Card style={{ width: "900px" }}>
                  <Card.Content style={{ background: "#406ccd" }}>
                    <Card.Header>NFT</Card.Header>
                    <Card.Meta>Portfolio of top NFT tokens</Card.Meta>
                    <Card.Description>
                      <Button onClick={this.createPair} color="green" type="submit" style={{ margin: "20px" }}>Create Pool!</Button><br></br>
                      TokenA: {this.state.tokenA}<br></br>
                      TokenB: {this.state.tokenB}<br></br>
                      <Button onClick={this.mint} color="green" type="submit" style={{ margin: "20px" }}>Mint!</Button><br></br>
                      <Button onClick={this.approveRouter} color="green" type="submit" style={{ margin: "20px" }}>Approve!</Button><br></br>
                      <Button onClick={this.addLiquidity} color="green" type="submit" style={{ margin: "20px" }}>Add Liquidity!</Button><br></br>
                    </Card.Description>
                  </Card.Content>
                </Card>
              </Card.Group>

            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div >
    );
  }
}

export default App;
