import React, { Component } from "react";
import Factory from "./abis/Factory.json";
import Router from "./abis/Router.json";
import CreateTokens from "./abis/CreateTokens.json";
import TokenA from "./abis/TokenA.json"
import TokenB from "./abis/TokenB.json"
import detectEthereumProvider from '@metamask/detect-provider';
import Web3 from 'web3';
import { Button, Header, HeaderSubheader } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';


import "./App.css";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      account: '',
      FacforyContract: null,
      RouterContract: null,
      TokenContract: null,
      address: "",
      tokenA: "",
      tokenB: "",
      pairAddress: "",
    }
  }

  async componentDidMount() {
    await this.loadWeb3();
    await this.loadBlockchainData();
  }

  // first up is to detect ethereum provider
  async loadWeb3() {
    const provider = await detectEthereumProvider();

    // modern browsers
    if (provider) {
      console.log('Ethereum wallet is connected');

      window.web3 = new Web3(provider);
    } else {
      console.log('No ethereum wallet detected');
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3;
    const accounts = await window.web3.eth.getAccounts();
    this.setState({ account: accounts[0] })
    const FacforyContract = new web3.eth.Contract(Factory.abi, "0x6725F303b657a9451d8BA641348b6761A6CC7a17");
    const RouterContract = new web3.eth.Contract(Router.abi, "0xD99D1c33F9fC3444f8101754aBC46c52416550D1");
    const TokenContract = new web3.eth.Contract(CreateTokens.abi, "0x0C5a3dbA4c5C77Bac5935E60de4D708FE6eD625c");
    this.setState({ FacforyContract, RouterContract, TokenContract });
  }

  handleInputChange = (event) => {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value
    })
  }

  createPool = async() => {
    await this.state.TokenContract.methods.create().send({ from: this.state.account });
    const tokenA = await this.state.TokenContract.methods.tokenA().call();
    const tokenB = await this.state.TokenContract.methods.tokenB().call();
    const pairAddress = await this.state.FacforyContract.methods.createPair(tokenA, tokenB).call();
    this.setState({ tokenA, tokenB, pairAddress})
  }

  approveRouter = async() => {
    const routerAddress = await this.state.TokenContract.methods.getRouterAddresss().call();
    console.log(routerAddress)
  }

  render() {
    return (
      <div className="App">
        <br></br>
        <Header as='h1'>Velvet.Capital</Header>
        <HeaderSubheader as='h2'>Create and Burn</HeaderSubheader>

        <Button onClick={this.createPool} color="green" type="submit" style={{ margin: "20px" }}>Create Pool!</Button><br></br>

        <Button onClick={this.approveRouter} color="green" type="submit" style={{ margin: "20px" }}>Approve!</Button><br></br>

      </div >
    );
  }
}

export default App;
