import React, { Component } from "react";
import LiquidityPool from "./abis/LiquidityPool.json";
import detectEthereumProvider from '@metamask/detect-provider';
import Web3 from 'web3';
import { Grid, Button, Card, Header, HeaderSubheader, Form, Input } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';


import "./App.css";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      account: '',
      LPContract: null,
      address: "",
      defiToMint: 0,
      nftToMint: 0,
      defiBalance: 0,
      nftBalance: 0
    }
  }

  async componentDidMount() {
    await this.loadWeb3();
    await this.loadBlockchainData();
  }

  // first up is to detect ethereum provider
  async loadWeb3() {
    const provider = await detectEthereumProvider();

    // modern browsers
    if (provider) {
      console.log('Ethereum wallet is connected');

      window.web3 = new Web3(provider);
    } else {
      console.log('No ethereum wallet detected');
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3;
    const accounts = await window.web3.eth.getAccounts();
    this.setState({ account: accounts[0] })
    const LPContract = new web3.eth.Contract(LiquidityPool.abi, "0xaf3335B8Ee5b4d58bdC1395fb582cE1D037F624d");
    this.setState({ LPContract });
  }

  handleInputChange = (event) => {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value
    })
  }

  mintDeFiTokens = async() => {
    const v = this.state.defiToMint / 10;
    await this.state.LPContract.methods.mintDeFi(v).send({from: this.state.account});
  }

  mintNFTTokens = async() => {
    const v = this.state.nftToMint / 10;
    console.log(v)
    await this.state.LPContract.methods.mintNFT(v).send({from: this.state.account});
  }

  getDeFiBalance = async() => {
    const res = await this.state.LPContract.methods.getDeFiBalance().call({from: this.state.account});
    this.setState({
      defiBalance: res
    })
  }

  getNFTBalance = async() => {
    const res = await this.state.LPContract.methods.getNFTBalance().call({from: this.state.account});
    this.setState({
      nftBalance: res
    })
  }

  render() {
    return (
      <div className="App">
        <br></br>
        <Header as='h1'>Velvet.Capital</Header>

        <Grid divided='vertically'>
          <Grid.Row columns={2} style={{ margin: "20px" }}>
            <Grid.Column>

              <Card.Group>
                <Card style={{ width: "900px" }}>
                  <Card.Content style={{ background: "#406ccd" }}>
                    <Card.Header>DeFi</Card.Header>
                    <Card.Meta>Top 5 DeFi tokens</Card.Meta>
                    <Card.Description>

                      <p>Mint amount of the two created tokens listed on the top!</p>
                      <Form onSubmit={this.mintDeFiTokens}>
                        <Input style={{ width: "300px", padding: 3 }} required type="text" placeholder="Amount to mint" name="defiToMint" onChange={this.handleInputChange}></Input><br></br>
                        <Button color="green" type="submit" style={{ margin: "20px" }}>Mint</Button>
                      </Form>

                      <Button onClick={this.getDeFiBalance} color="green" type="submit" style={{ margin: "20px" }}>Get DeFi Balance!</Button>
                      <p>{this.state.defiBalance}</p>

                    </Card.Description>
                  </Card.Content>
                </Card>
              </Card.Group>
            </Grid.Column>


            <Grid.Column>
              <Card.Group>
                <Card style={{ width: "900px" }}>
                  <Card.Content style={{ background: "#406ccd" }}>
                    <Card.Header>NFT</Card.Header>
                    <Card.Meta>Top 5 NFT tokens</Card.Meta>
                    <Card.Description>

                      <p>Mint amount of the two created tokens listed on the top!</p>
                      <Form onSubmit={this.mintNFTTokens}>
                        <Input style={{ width: "300px", padding: 3 }} required type="text" placeholder="Amount to mint" name="nftToMint" onChange={this.handleInputChange}></Input><br></br>
                        <Button color="green" type="submit" style={{ margin: "20px" }}>Mint!</Button>
                      </Form>

                      <Button onClick={this.getNFTBalance} color="green" type="submit" style={{ margin: "20px" }}>Get NFT Balance!</Button>
                      <p>{this.state.nftBalance}</p>


                    </Card.Description>
                  </Card.Content>
                </Card>
              </Card.Group>

            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div >
    );
  }
}

export default App;
