import React, { Component } from "react";
import LiquidityPool from "./abis/LiquidityPool.json";
import detectEthereumProvider from '@metamask/detect-provider';
import Web3 from 'web3';
import { Grid, Button, Card, Header, HeaderSubheader, Form, Input } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';


import "./App.css";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      account: '',
      LPContract: null,
      LPContract2: null,
      address: "",
      tokenA: "Token hasn't been created yet!",
      tokenB: "Token hasn't been created yet!",
      pairAddress: "",
      valueToMint: 0,
      valueToApprove: 0,
      valueToAdd: 0,
      tokenA2: "Token hasn't been created yet!",
      tokenB2: "Token hasn't been created yet!",
      pairAddress2: "",
      valueToMint2: 0,
      valueToApprove2: 0,
      valueToAdd2: 0
    }
  }

  async componentDidMount() {
    await this.loadWeb3();
    await this.loadBlockchainData();
  }

  // first up is to detect ethereum provider
  async loadWeb3() {
    const provider = await detectEthereumProvider();

    // modern browsers
    if (provider) {
      console.log('Ethereum wallet is connected');

      window.web3 = new Web3(provider);
    } else {
      console.log('No ethereum wallet detected');
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3;
    const accounts = await window.web3.eth.getAccounts();
    this.setState({ account: accounts[0] })
    const LPContract = new web3.eth.Contract(LiquidityPool.abi, "0x207315B508E44FF6Cf4384426b5E3356b1E6E281");
    this.setState({ LPContract });
  }

  handleInputChange = (event) => {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value
    })
  }

  createPair = async() => {
    await this.state.LPContract.methods.createNewPair().send({from: this.state.account});

    const t1 = await this.state.LPContract.methods.tokenA().call()
    const t2 = await this.state.LPContract.methods.tokenB().call()

    this.setState({
      tokenA: t1,
      tokenB: t2
    })
  }

  createPair2 = async() => {
    await this.state.LPContract.methods.createNewPair().send({from: this.state.account});

    const t1 = await this.state.LPContract2.methods.tokenA().call()
    const t2 = await this.state.LPContract2.methods.tokenB().call()

    this.setState({
      tokenA2: t1,
      tokenB2: t2
    })
  }

  approveRouter = async() => {
    await this.state.LPContract.methods.approveRouter(10000).send({from: this.state.account});
  }

  approveRouter2 = async() => {
    await this.state.LPContract2.methods.approveRouter(10000).send({from: this.state.account});
  }

  mint = async() => {
    await this.state.LPContract.methods.mint(10000).send({from: this.state.account});
  }

  mint2 = async() => {
    await this.state.LPContract2.methods.mint(10000).send({from: this.state.account});
  }

  addLiquidity = async() => {
    await this.state.LPContract.methods.addLiquidity(10000).send({from: this.state.account});
  }

  addLiquidity2 = async() => {
    await this.state.LPContract2.methods.addLiquidity(10000).send({from: this.state.account});
  }

  render() {
    return (
      <div className="App">
        <br></br>
        <Header as='h1'>Velvet.Capital</Header>

        <Grid divided='vertically'>
          <Grid.Row columns={2} style={{ margin: "20px" }}>
            <Grid.Column>

              <Card.Group>
                <Card style={{ width: "900px" }}>
                  <Card.Content style={{ background: "#406ccd" }}>
                    <Card.Header>DeFi</Card.Header>
                    <Card.Meta>Portfolio of top DeFi projects</Card.Meta>
                    <Card.Description>
                      <p>TokenA: {this.state.tokenA}<br></br></p>
                      <p>TokenB: {this.state.tokenB}<br></br></p>

                      <Button onClick={this.createPair} color="green" type="submit" style={{ margin: "20px" }}>Create Tokens / LP!</Button><br></br>

                      <p>Mint amount of the two created tokens listed on the top!</p>
                      <Form onSubmit={this.mint}>
                        <Input style={{ width: "300px", padding: 3 }} required type="text" placeholder="Amount to mint" name="valueToMint" onChange={this.handleInputChange}></Input><br></br>
                        <Button color="green" type="submit" style={{ margin: "20px" }}>Mint</Button>
                      </Form>
                      <p>Approve router to put the tokens into the LP!</p>
                      <Form onSubmit={this.approveRouter}>
                        <Input style={{ width: "300px", padding: 3 }} required type="text" placeholder="Amount to approve" name="valueToApprove" onChange={this.handleInputChange}></Input><br></br>
                        <Button color="green" type="submit" style={{ margin: "20px" }}>Approve!</Button>
                      </Form>

                      <p>The amount has to be approved before it can be put into the LP!</p>
                      <Form onSubmit={this.addLiquidity}>
                        <Input style={{ width: "300px", padding: 3 }} required type="text" placeholder="Amount to add to LP" name="valueToAdd" onChange={this.handleInputChange}></Input><br></br>
                        <Button color="green" type="submit" style={{ margin: "20px" }}>Approve!</Button>
                      </Form>
                    </Card.Description>
                  </Card.Content>
                </Card>
              </Card.Group>
            </Grid.Column>


            <Grid.Column>
              <Card.Group>
                <Card style={{ width: "900px" }}>
                  <Card.Content style={{ background: "#406ccd" }}>
                    <Card.Header>NFT</Card.Header>
                    <Card.Meta>Portfolio of top NFT projects</Card.Meta>
                    <Card.Description>
                      <p>TokenA: {this.state.tokenA}<br></br></p>
                      <p>TokenB: {this.state.tokenB}<br></br></p>

                      <Button onClick={this.createPair} color="green" type="submit" style={{ margin: "20px" }}>Create Tokens / LP!</Button><br></br>

                      <p>Mint amount of the two created tokens listed on the top!</p>
                      <Form onSubmit={this.mint}>
                        <Input style={{ width: "300px", padding: 3 }} required type="text" placeholder="Amount to mint" name="valueToMint2" onChange={this.handleInputChange}></Input><br></br>
                        <Button color="green" type="submit" style={{ margin: "20px" }}>Mint</Button>
                      </Form>

                      <p>Approve router to put the tokens into the LP!</p>
                      <Form onSubmit={this.approveRouter}>
                        <Input style={{ width: "300px", padding: 3 }} required type="text" placeholder="Amount to approve" name="valueToApprove2" onChange={this.handleInputChange}></Input><br></br>
                        <Button color="green" type="submit" style={{ margin: "20px" }}>Approve!</Button>
                      </Form>

                      <p>The amount has to be approved before it can be put into the LP!</p>
                      <Form onSubmit={this.addLiquidity}>
                        <Input style={{ width: "300px", padding: 3 }} required type="text" placeholder="Amount to add to LP" name="valueToAdd2" onChange={this.handleInputChange}></Input><br></br>
                        <Button color="green" type="submit" style={{ margin: "20px" }}>Approve!</Button>
                      </Form>
                    </Card.Description>
                  </Card.Content>
                </Card>
              </Card.Group>

            </Grid.Column>
          </Grid.Row>
        </Grid>
      </div >
    );
  }
}

export default App;
