      this.setState({
        connected: true
      });
    }

    this.setState({
      account: accounts[0]
    });
    const SwapContract = new web3.eth.Contract(IndexSwap.abi, "0xc9b6F4c458795887D1D4421772c524B88dE4dfe4");
