    const DeFiTokenContract = new web3.eth.Contract(IndexToken.abi, "0x6E49456f284e3da7f1515eEE120E2706cab69fD5");
    this.setState({
      SwapContract,
      NFTTokenContract,
      DeFiTokenContract,
      NFTPortfolioContract
    });
  }

